/**
 * Enum for item update report update types.
 * This is a string type enum.
*/
enum ITEM_UPDATE_TYPE {
    /**
    * There is no update available for that item.
    */
     NoUpdate = "NoUpdate",
    
     /**
      * There is an update available for that item.
     */
     ItemUpdate = "ItemUpdate",
    
    /**
      * There is an update available for the configurationState only.
     */
    ConfigurationOnlyUpdate = "ConfigurationOnlyUpdate",
    
     /**
      * That item is not found in the latest catalog.
     */
     NoMatchingItemFound = "NoMatchingItemFound",
    
     /**
      * There is no catalog that could be found.
     */
     NoMatchingCatalogFound = "NoMatchingCatalogFound"
}

/**
 * Interface for the item update report.
*/
interface IItemUpdateReport {
    /**
     * An array of item update report entries.
    */
    entries: Array<IItemUpdateReportEntry>;
}

/**
 * Interface for the item update report entry.
*/
interface IItemUpdateReportEntry {
    /**
     * The unique instance id of the entry.
    */
    instanceId: string,

    /**
     * The update type of the entry.
    */
    updateType: ITEM_UPDATE_TYPE,

    /**
     * The returned item variant.
    */
    itemVariant: IItemVariant | void
}